pragma solidity  0.5.16;

contract BettingDapp {
    address public owner;
    string public competitor1;
    string public competitor2;
    uint public betAmount;
    uint public totalBets;
    uint public winner;
    bool public betsLocked;
    bool public roundComplete;
    
    struct Bet {
        address payable bettor;
        uint amount;
        bool isWinner;
    }
    
    Bet[] public bets;
    mapping(address => uint) public bettors;
    
    constructor(string memory _competitor1, string memory _competitor2, uint _betAmount) public {
        owner = msg.sender;
        competitor1 = _competitor1;
        competitor2 = _competitor2;
        betAmount = _betAmount;
    }
    
    function placeBet(bool _isWinner) public payable {
        require(msg.value == betAmount, "Please bet the correct amount");
        require(!betsLocked, "Betting is locked for this round");
        require(bettors[msg.sender] == 0, "You have already placed a bet");
        
        Bet memory newBet = Bet(msg.sender, msg.value, _isWinner);
        bets.push(newBet);
        bettors[msg.sender] = bets.length;
        totalBets += msg.value;
    }
    
    function lockBets() public {
        require(msg.sender == owner, "Only the owner can lock bets");
        require(!betsLocked, "Betting is already locked for this round");
        
        betsLocked = true;
    }
    
    function completeRound(uint _winner) public {
        require(msg.sender == owner, "Only the owner can complete a round");
        require(betsLocked, "Betting is not yet locked for this round");
        require(!roundComplete, "Betting round is already complete");
        
        winner = _winner;
        roundComplete = true;
    }
    
    function distributeWinnings() public {
        require(roundComplete, "Betting round is not yet complete");
        
        uint totalLosers = 0;
        uint totalWinners = 0;
        
        for (uint i = 0; i < bets.length; i++) {
            if (bets[i].isWinner) {
                totalWinners += bets[i].amount;
            } else {
                totalLosers += bets[i].amount;
            }
        }
        
        for (uint j = 0; j < bets.length; j++) {
            if (bets[j].isWinner) {
                uint payout = bets[j].amount * totalLosers / totalWinners;
                bets[j].bettor.transfer(payout + bets[j].amount);
            }
        }
    }
}
